(* Stealing this bit from wikipedia *)
(* https://en.wikipedia.org/wiki/Hyperoperation *)

Fixpoint inner (f : nat -> nat) (n a b : nat) :=
  match b with
  | 0 => match n with
         | 0 => a
         | 1 => 0
         | _ => 1
         end
  | S b' =>
    f (inner f n a b')
  end.

Fixpoint ack (n a b : nat) :=
  match n with
  | 0 => S b
  | S n' =>
    inner (ack n' a) n' a b
  end.

